kickstart.pre:
from rocks import rocks_partition
membership = '&membership;' nodename = '&hostname;'
def doCompute(file, disk): file.write('zerombr\n') file.write('clearpart --all --drives=%s --initlabel\n' % disk) file.write('part / --fstype=ext4 --size=62500 --ondisk=%s\n' % disk) file.write('part raid.00 --size=1 --grow --ondisk=%s\n' % disk)
def doDevel(file, disk): file.write('zerombr\n') file.write('clearpart --all --drives=%s --initlabel\n' % disk) file.write('part / --fstype=ext4 --size=31250 --ondisk=%s\n' % disk) file.write('part /state/partition1 --fstype=ext4 --size=1 --grow --ondisk=%s\n' % disk)
# # main # p = rocks_partition.RocksPartition() disks = p.getDisks()
file = open('/tmp/user_partition_info', 'w')
if membership in [ 'Compute' ]: doCompute(file, disks[0])
if membership in [ 'Development Appliance' ]: doDevel(file, disks[0])
file.close()
